int queue[5];
int num_readers[10];
int num_fixed[10];
int ct=1;
int wct=0;
sem_t check_wr;
sem_t binary_sem[5];
sem_t wr[5];
sem_t fixed[5];
//atomic_t f;
//atomic_t r;
volatile int f;
volatile int r;
#define LEN 5
